$(function(){downList($(".nav-name"));showSelect($(".nav-list"));showWindow($(".leave-btn-list .no"));cancel($(".reason-btn .cancel"))});function downList(a){a.on("click",function(){a.next("ul").animate({height:"0",overflow:"hidden"});a.removeClass("active");$(this).addClass("active").next("ul").animate({height:"12rem",overflowY:"scroll"});$(".shadow").show()});$(".shadow").on("tap",function(){a.next("ul").animate({height:"0",overflow:"hidden"});a.removeClass("active");$(this).hide()})}function showSelect(a){a.find("a").on("click",function(){var b=$(this).text();a.find("a").removeClass("active");$(this).addClass("active").parents("ul").prev().removeClass("active").text(b);a.animate({height:"0",overflow:"hidden"});$(".shadow").hide();return false})}function showWindow(a){a.on("click",function(){$(".show-window").animate({bottom:0},500);$(".shadow").show();return false})}function cancel(a){a.on("tap",function(){$(this).parents(".show-window").animate({bottom:"-60%"},500);$(".shadow").hide()})};