$(function(){$(".cancel").on("click",function(){$(".select-contact").hide();$(window).off("scroll")});$(".share").on("click",function(){$(".select-contact").show();var b=$("input");setHe();setRadioId(b);setClickStyle();showSelect();var a=$("body").scrollTop();$(window).on("scroll",function(){$(this).scrollTop(a)})})});function setHe(){var c=$("#contact-static");var a=$("#contact-list");var b=$(window).height();$(".contact-wrap").height(b);var d=c.height();a.css("margin-top",d+"px")}function showSelect(){var c=$(".select-con");var b=$("input[type=checkbox]:checked");var a="";c.html("");$.each(b,function(e,d){a=$(d).parents("li").find(".contact-hd").html();c.append("<li>"+a+"</li>")});setHe()}function setClickStyle(){$("input[type=checkbox]").on("click",function(){if($(this).is(":checked")){$(this).siblings("label").addClass("active")}else{$(this).siblings("label").removeClass("active")}showSelect()})}function setRadioId(b){var a=randomNum(b.length);$.each(b,function(c,d){$(d).attr("id","checkBox"+a[c]);$(d).siblings("label").attr("for","checkBox"+a[c])})}function randomNum(a){var d=[];for(var c=0;c<a;c++){d[c]=parseInt(Math.random()*a);for(var b=0;b<c;b++){if(d[c]==d[b]){c--;break}}}return d};