var timeCon=$(".countdown-time");$(function(){diffDay("2016/6/7");goNav();goTop();$("label[for=show-answer]").on("click",function(){var a=$(this);checkbox(a)});$("label[for=select-all]").on("click",function(){var a=$(this);checkbox(a)})});var nav=$(".hd-subnav");nav.on("mouseenter",function(){$(this).find(".sld-area").stop().slideDown(100)});nav.on("mouseleave",function(){$(this).find(".sld-area").stop().slideUp(100)});function diffDay(c){var a=new Date();var e=new Date(c);var b=e-a;var d=0;if(b>0){d=parseInt(b/1000/3600/24);if(d<=1000){timeCon.find(".first").html(parseInt(d/100));timeCon.find(".second").html(parseInt((d/10)%10));timeCon.find(".third").html(parseInt((d%100)%10))}}else{timeCon.html("高考过了")}}function goNav(){$(".nav-link a").on("click",function(){var a=$(this);linkSkip(a)});$(".box-list a").on("click",function(){var a=$(this);linkSkip(a)})}function linkSkip(c){var a=c.attr("href");c.addClass("active").parent("li").siblings("li").find("a").removeClass("active");var b=$(a).offset().top;$("html,body").animate({scrollTop:b+0},800);return false}var $goTop=$(".go-top");$(window).on("resize",function(){if($(window).width()<1400){$goTop.css("right","10px")}});function goTop(){$goTop.on("click",function(){$("html,body").animate({scrollTop:0},800)})}function checkbox(a){var b=a.attr("for");if(!$("#"+b).prop("checked")){a.addClass("click")}else{a.removeClass("click")}};